const redux = require('redux')


const ReduxFunc = (state={counter:1},action)=>{
    if(action.type==='increament'){
        return(state.counter +5)
    }
    else if (action.type==='decrement'){
        return(state.counter+5)
    }

}

const Store  = redux.createStore(ReduxFunc)
export default Store
